interface IconProps extends SVGProps<SVGSVGElement> {
  name: IconName;
  size?: number;
  color?: string;
}

const Icon: FC<IconProps> = ({
  name,
  size = 20,
  color = "currentColor",
  style,
  ...props
}) => {
  const IconComponent = icons[name];

  if (!IconComponent) {
    console.warn(`Icon "${name}" not found`);
    return null;
  }

  return (
    <IconComponent
      width={size}
      height={size}
      style={{ color, ...style }}
      {...props}
    />
  );
};

export default Icon;
