interface IconProps extends SVGProps<SVGSVGElement> {
  name: IconName;
  size?: number;
  width?: number;
  height?: number;
  color?: string;
}

const Icon: FC<IconProps> = ({
  name,
  size = 20,
  width,
  height,
  color = "currentColor",
  style,
  ...props
}) => {
  const IconComponent = icons[name];

  if (!IconComponent) {
    console.warn(`Icon "${name}" not found`);
    return null;
  }

  return (
    <IconComponent
      width={width ?? size}
      height={height ?? size}
      style={{ color, ...style }}
      {...props}
    />
  );
};

export default Icon;
